import { useEffect, useRef } from "react"

export default function FriendCard({name, unreadMessage, avatar}) {
    const server = useRef();

    useEffect(() => {
        server.current = process.env.REACT_APP_SEVER_IP + ":" + process.env.REACT_APP_SERVER_PORT
    }, [])

    return (
        <li className="row">
            <div className="wrap-avatar">
                <span className="activity-status activity-status-online" />
                <img
                    className="avatar-small"
                    src={`${server.current}/uploads/${avatar}`}
                    alt="avatar"
                />
            </div>
            <p className="ml-10 flex-1">{name}</p>
            <span className="count-unread-message">{unreadMessage}</span>
        </li>
    )
}
