import React, { useEffect, useState } from 'react';
import { sendAccount } from '../adapters/account';

function Register() {

    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [confirmPassword, setConfirmPassword] = useState("");
    const [isValid, setIsValid] = useState(false);

    useEffect(() => {
        import('../styles/login.css')
    }, [])

    function handleInputChange(e) {
        const { id, value } = e.target;

        if (id === "firstName") {
            setFirstName(value);
        }
        if (id === "lastName") {
            setLastName(value);
        }
        if (id === "username") {
            setUsername(value)
        }
        if (id === "password") {
            setPassword(value);
        }
        if (id === "confirmPassword") {
            setConfirmPassword(value)
        }

        if (firstName !== "" && lastName !== "" && username !== "" && password !== "" && password === confirmPassword) {
            setIsValid(true);
        } else {
            setIsValid(false);
        }
    }

    async function handleSubmit(e) {
        e.preventDefault();

        const account = {
            'firstName': firstName,
            'lastName': lastName,
            'username': username,
            'password': password
        }
        
        if (isValid)
        {
            sendAccount(account)
        }
    }

    return (
        <div className="wrapper">
            <form onSubmit={handleSubmit}>
                <h1>Register</h1>
                <div className="input-box row">
                    <input id='firstName'
                        className='mr-15'
                        type="text"
                        placeholder="First Name"
                        value={firstName}
                        onChange={(e) => handleInputChange(e)}
                    />
                    <input id='lastName'
                        type="text"
                        placeholder="Last Name"
                        value={lastName}
                        onChange={(e) => handleInputChange(e)}
                    />
                </div>
                <div className="input-box">
                    <input id='username'
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => handleInputChange(e)}
                    />
                    <span id="img-user" className="text-white material-symbols-outlined">
                        person
                    </span>
                </div>
                <div className="input-box">
                    <input id="password"
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => handleInputChange(e)}
                    />
                    <span id="img-lock" className="text-white material-symbols-outlined">
                        key
                    </span>
                </div>
                <div className="input-box">
                    <input
                        id="confirmPassword"
                        type="password"
                        placeholder="Confirm Password"
                        value={confirmPassword}
                        onChange={(e) => handleInputChange(e)}
                    />
                    <span id="img-lock" className="text-white material-symbols-outlined">
                        key
                    </span>
                </div>
                <button type="submit" className={
                    (isValid) ? 'btn' : 'btn btn-disable'
                }>
                    Register
                </button>
            </form>
        </div>
    )
}

export default Register;
