import { useState, useEffect } from 'react';
import { getPost } from '../adapters/post'
import Post from './Post'
import { shouldReload, setShouldReload } from '../utils'; // Import state and function
export default function PostList() {
    const [post, setPost] = useState([]);
    const [shouldReload, setShouldReload] = useState(false); // Thêm biến trạng thái này

    useEffect(() => {
        (
            async function () {
                const post = await getPost();
                setPost(post);

                setShouldReload(false);
            }
           
        )()

        if (shouldReload) {
            fetchData();
        }

    }, [shouldReload]);

    return (
        <>
            {
                post.map((item, index) => (
                    <Post
                        key={index}
                        user={{
                            name: item.sender,
                            image: "https://antimatter.vn/wp-content/uploads/2022/10/hinh-anh-gai-xinh-de-thuong-457x600.jpg"
                        }}
                        content={{
                            time: "12 hours ago",
                            image: (item.content.image !== "") ? `http://127.0.0.1:3001/uploads/${item.content.image}` : ""
                        }}
                        creationTime={item.creationTime}
                    />
                ))
            }
        </>
    )
}
