import React from 'react';
import { Container, Row, Col, Button, Jumbotron } from 'react-bootstrap';
import Countdown from 'react-countdown-now';



export default function NoFriend() {
  // Set your countdown end time here
  const countdownEndTime = new Date();
  countdownEndTime.setHours(countdownEndTime.getHours() + 35);

  return (
    <Container className="d-flex justify-content-center align-items-center" style={{ height: '100vh' }}>
      <Row>
        <Col>
          <Jumbotron>
            <h1>Coming Soon</h1>
            <p>Our website is under construction. We'll be back soon!</p>
            <p>
              <Button variant="primary" href="#">Get Notified</Button>
            </p>
          </Jumbotron>
        </Col>
      </Row>
    </Container>
  );
};
