import { useState, useEffect } from 'react';
import { getPost } from '../adapters/post'
import Post from './Post'
import io from 'socket.io-client';

const socket = io('http://127.0.0.1:4000', { transports : ['websocket'] });

export default function PostList() {
    const [post, setPost] = useState([]);
    
    useEffect(() => {
        const handleNewPost = () => {
            setNewPostEventCount(newPostEventCount + 1);
        };
    
        socket.on('newPost', handleNewPost);
    
        // Gọi hàm update() khi component được render lần đầu.
        update();
    
        return () => {
            // Loại bỏ sự kiện lắng nghe khi component bị unmount.
            socket.off('newPost', handleNewPost);
        };
    }, [newPostEventCount]);
    
    useEffect(() => {
        if (newPostEventCount > 0) {
            update();
        }
    }, [newPostEventCount]);

    async function update () {
        const post = await getPost();
        setPost(post);
    }

    return (
        <>
            {
                post.map((item, index) => (
                    <Post
                        key={index}
                        user={{
                            name: item.sender,
                            image: "https://antimatter.vn/wp-content/uploads/2022/10/hinh-anh-gai-xinh-de-thuong-457x600.jpg"
                        }}
                        content={{
                            time: "12 hours ago",
                            image: (item.content.image !== "") ? `http://127.0.0.1:3001/uploads/${item.content.image}` : ""
                        }}
                        creationTime={item.creationTime}
                    />
                ))
            }
        </>
    )
}
