import { useEffect, useState } from "react"

/*
message = 
{
  "_id": {
    "$oid": "652bbc19a26b30172fa34f59"
  },
  "sender": {
    "$oid": "65253691fc11231d37f45a3a"
  },
  "recipients": {
    "$oid": "652a1ec8d0960eece31bc32d"
  },
  "timestamp": "Sat Oct 14 2023 07:14:01 GMT+0700 (Indochina Time)",
  "content": "Đi chơi không ?"
}
*/
const server = process.env.REACT_APP_SEVER_IP + ":" + process.env.REACT_APP_SERVER_PORT

export default function Message({ owner, chat, currentUser }) {
    const [fullName, setFullName] = useState("")
    const [avatar, setAvatar] = useState("")
    const [mess, setMess] = useState(chat.content)

    useEffect(() => {
        if (chat.messages.sender_id == currentUser._id)
        {
            const fullName = currentUser.firstName + " " + currentUser.lastName
            const avatar = currentUser.avatar
            setFullName(fullName)
            setAvatar(server + "/uploads/" + avatar)
        }
        else 
        {
            const fullName = chat.recipients.firstName + " " + chat.recipients.lastName
            const avatar = chat.recipients.avatar
            setFullName(fullName)
            setAvatar(server + "/uploads/" + avatar)
        }

    }, [])

    const renderComponent = () => {
        if (owner == "my") {
            return (
                <div className="comment comment-right">
                    <div className="box">
                        <b className="sender">{fullName}</b>
                        <p className="text">{mess}</p>
                    </div>
                    <img
                        className="avatar"
                        src={avatar}
                        alt="avatar"
                    />
                </div>
            )
        }
        else if (owner == "other") {
            return (
                <div className="comment comment-left">
                    <img
                        className="avatar"
                        src={avatar}
                        alt="avatar"
                    />
                    <div className="box">
                        <b className="sender">{fullName}</b>
                        <p className="text">{mess}</p>
                    </div>
                </div>
            )
        }
    }

    return (renderComponent())
}
