import { useEffect, useRef, useState } from "react"
import generalStyle from '../styles/general.css'

export default function FriendCard({name, unreadMessage, avatar, content}) {
    const [server, setServer] = useState("")

    useEffect(() => {
        setServer(process.env.REACT_APP_SEVER_IP + ":" + process.env.REACT_APP_SERVER_PORT)
    }, [])

    return (
        <li className="row">
            <div className="wrap-avatar">
                <span className="activity-status activity-status-online" />
                <img
                    className="avatar-small"
                    src={`${server}/uploads/${avatar}`}
                    alt="avatar"
                />
            </div>
           <div>
                <p className="ml-10 flex-1">{name}</p>
                <span className={`${generalStyle['mt-5']}`}>{content}</span>

           </div>
            <span className="count-unread-message">{unreadMessage}</span>
        </li>
    )
}
