import { useEffect, useState } from "react";
import { addComment } from "../adapters/comment";
import TextareaAutosize from 'react-textarea-autosize';
import socket from '../socket/socket-client'

// parameter updateComment is hook const [, set]
export default function FormComment({ sender, postID, updateComment, setUpdateComment }) {
    const [message, setMessage] = useState("")
    const [commentAddedListener, setCommentAddedListener] = useState(null);
    useEffect(() => {
       
    }, [])

    const handleMessageChange = event => {
        // 👇️ access textarea value
        setMessage(event.target.value);

    };

    function postComment() {
        if (sender && sender._id) {
            const comment = {
                postID: postID,
                content: message,
                sender: sender._id,
            };

            if (isValidComment()) {
                addComment(comment);

                if (commentAddedListener === null) {
                    const newCommentAddedListener = (comment) => {
                        const listComment = [comment].concat(updateComment);
                        setUpdateComment(listComment);
                        console.log("Comment vừa được thêm", comment);
                    };

                    socket.on("commentAdded", newCommentAddedListener);
                    setCommentAddedListener(newCommentAddedListener);
                }
            }
        } else {
            console.error("Sender is null or missing _id.");
        }
    }

    function isValidComment()
    {
        if (message.length == 0)
            return false;
        return true;
    }

    return (
        <div className="form-comment row">
            <img
                className="avatar"
                src={
                    (!!sender.avatar) 
                    ? `http://127.0.0.1:3001/uploads/${sender.avatar}`
                    : "http://127.0.0.1:3001/uploads/anonymous.png"
                }
                alt="avatar"
            />
            <div>
                <TextareaAutosize 
                    className="post-comment ml-13"
                    placeholder="Bạn đang nghĩ gì ?"
                    onChange={handleMessageChange} />
                <div className="icon-comment">
                    <ul className="list-icon row">
                        <li>
                            <img
                                id="js-upload-picture"
                                className="icon-heading"
                                src="https://img.icons8.com/pulsar-color/48/pictures-folder.png"
                                alt="pictures-folder"
                            />
                        </li>
                        <li>
                            <img
                                className="icon-heading"
                                src="https://img.icons8.com/pulsar-color/48/pictures-folder.png"
                                alt="pictures-folder"
                            />
                        </li>
                    </ul>
                    <div id="js-btn-post" className="btn btn-blue" onClick={postComment}>
                        Post
                    </div>
                </div>
            </div>
        </div>
    )
}
