import React, { useState, useEffect } from "react";
import { getPost } from "../adapters/post";
import Post from "./Post";
import io from "socket.io-client";

const socket = io("http://127.0.0.1:4000", { transports: ["websocket"] });

export default function PostList() {
    const [post, setPost] = useState([]);
    useEffect(() => {
        // Gửi thông báo đến server để lấy bài viết
        socket.emit("getpost");

        const handleGetPost = (newPost) => {
            setPost(newPost);
           // console.log(newPost)
        };

        const handlePostAdded = (newPost) => {
            setPost(newPost);

        };

        // Gắn sự kiện "getpost" và "postAdded" một lần
        socket.once("getpost", handleGetPost);
        socket.on("postAdded", handlePostAdded);

        return () => {
            // Hủy đăng ký sự kiện khi component bị hủy
            socket.off("getpost", handleGetPost);
            socket.off("postAdded", handlePostAdded);
        };
    }, []);
    // useEffect(() => {
    //     // Gửi thông báo đến server để lấy bài viết
    //     socket.emit("getpost")
    //     console.log("get post")
    //     socket.on("getpost", (newPost) => {
    //         console.log(1)
    //         setPost(newPost);

    //     });

    //     // Nhận thông báo từ máy chủ nếu có bài viết mới
    //     socket.on("postAdded", (newPost) => {
    //         setPost(newPost);
    //     });

    // }, []);

    return (
        <>
            {
                post.map((item, index) => (
                    <Post
                        key={index}
                        user={{
                            name: item.sender.firstName + " " + item.sender.lastName,
                            image: item.sender.avatar !== "" ? `http://127.0.0.1:3001/uploads/${item.sender.avatar}` : "",
                        }}
                        content={{
                            text: item.content.text,
                            image: item.content.image !== "" ? `http://127.0.0.1:3001/uploads/${item.content.image}` : "",
                        }}
                        creationTime={item.creationTime}
                        comments={item.comments}
                    />
                ))
            }
            {/* Thêm giao diện để tạo bài viết mới và gọi handleNewPost khi bài viết được tạo */}
        </>
    );
}
