import React, { useEffect, useRef, useState } from "react";
import Headers from '../components/Header';
import ListChat from "../components/ListChat";
import TextareaAutosize from 'react-textarea-autosize';
import socket from "../socket/socket-client";
import Message from "../components/Message";

// Import CSS styles
import chatStyle from '../styles/chat.css';
import homepageStyle from '../styles/homepage.css';
import generalStyle from '../styles/general.css';

export default function Chat() {
    // State variables
    const [server, setServer] = useState("");
    const [message, setMessage] = useState("");
    const [selectedFriend, setSelectedFriend] = useState(null);
    const [currentUser, setCurrentUser] = useState(null);
    const [otherUser, setOtherUser] = useState(null);
    const [chatList, setChatList] = useState([])

    const userRef = useRef();

    useEffect(() => {

    }, [selectedFriend]);

    useEffect(() => {
        const session = JSON.parse(sessionStorage.getItem("userAuth"));
        userRef.current = session.user;
        setServer(`${process.env.REACT_APP_SEVER_IP}:${process.env.REACT_APP_SERVER_PORT}`);

    }, []);

    useEffect(() => {

        if (selectedFriend) {
            if (selectedFriend.receiver._id !== userRef.current.id) {
                setOtherUser(selectedFriend.receiver);
                setCurrentUser(userRef.current);
            } else {
                setCurrentUser(userRef.current);
                setOtherUser(selectedFriend.receiver);
            }
            
        }
    }, [selectedFriend])

    useEffect(() => {
        socket.on("updateMessage", function (message) {
            setChatList(message)

           
        })
    }, [])

    function sendMessage() {
        const data = {
            'idSender': currentUser._id,
            'idRecipient': otherUser._id,
            'content': message
        }

        socket.emit("sendMessage", data);
    }

    return (
        <div id="app">
            <Headers customStyle='header-chat' title="Message" />

            <div id="wrap-message" className="row">
                <ListChat setSelectedFriend={setSelectedFriend} />

                {/* Chat Box */}
                <div className="parent flex-1 mt-15">
                    <div id="boxChat" className="row justify-space-between mlr-32">

                        {/* Avatar and Name */}
                        <div className="row align-items-center">
                            <img
                                className="avatar"
                                src={
                                    selectedFriend ? (selectedFriend.receiver._id !== userRef.current.id ? `${server}/uploads/${selectedFriend.receiver.avatar}` : `${server}/uploads/${userRef.current.avatar}`) : `${server}/uploads/anonymous.png`
                                }
                                alt="avatar"
                            />

                            <div className="ml-15">
                                <h3>{otherUser ? `${otherUser.firstName} ${otherUser.lastName}` : currentUser ? `${currentUser.firstName} ${currentUser.lastName}` : ""}</h3>
                                <p>Nhìn gì cha</p>
                            </div>
                        </div>

                        {/* List Icons */}
                        <div className="row">
                            <a className="icon-effect" href="#">
                                <i className="fa-regular fa-envelope" />
                            </a>
                            <a className="icon-effect" href="#">
                                <i className="fa-solid fa-gears" />
                            </a>
                            <a className="icon-effect" href="#">
                                <i className="fa-solid fa-gears" />
                            </a>
                        </div>
                    </div>

                    {/* Chat Messages View */}
                    <div id="viewchat" className="mlr-32">
                        {/* Messages will be displayed here */}
                        {chatList.map((chat, index) => (
                            <Message
                                owner={ (chat.sender_id === userRef.current.id) ? "my" : "other" }
                                message={chat}
                            />
                        ))}
                    </div>

                    {/* Message Input Box */}
                    <div id="boxMessage" className="row">
                        <TextareaAutosize
                            className="post-comment ml-13"
                            placeholder="Bạn đang nghĩ gì ?"
                            onChange={e => setMessage(e.target.value)}
                        />
                        <img onClick={sendMessage} id="btn" width="36" height="36" src="https://img.icons8.com/sf-regular/48/paper-plane.png" alt="paper-plane" />
                    </div>
                </div>
            </div>
        </div>
    )
}
