import FriendCard from './FriendCard'
import SearchBar from './SearchBar'
import { useEffect, useState, useRef } from 'react'
import socket from '../socket/socket-client'
import listFriendsStyle from '../styles/listFriends.css'

export default function ListChat({ setSelectedFriend }) {
  const [friends, setFriends] = useState([])

  const user = useRef()

  useEffect(() => {
    const session = JSON.parse(sessionStorage.getItem("userAuth"))
    user.current = session.user._id

    const recipients = ['65253691fc11231d37f45a3a', '652a1ec8d0960eece31bc32d']
    
    socket.emit("getListChat", recipients)

    function onListChat(friends) {
      setFriends(friends)
      
      setSelectedFriend(friends[0]) // Lấy user đầu tiên làm mặc định
    }

    socket.on("getListChat", onListChat)

    return () => {
      socket.off('getFriends', onListChat);
    };
  }, [])

  const handleFriendClick = (friend) => {
    setSelectedFriend(friend);
  }

  return (
    <div id="right-side-bar">
      <SearchBar placeholder="Search anything" />

      <div id='list-friends' className='wrap-list-item'>
        <div className="mt-25">
          <b className="heading ml-13">Friends</b>
          <ul className="list-item">
            {friends.map((friend, index) => (
              <li key={index} onClick={() => handleFriendClick(friend)} className="row justify-content-space-between">
               <FriendCard
                        name={friend.receiver._id === user.current  ? `${friend.sender.firstName} ${friend.sender.lastName}` : `${friend.receiver.firstName} ${friend.receiver.lastName}`}
                        unreadMessage={friend.unreadMessage}
                        avatar={friend.receiver._id === user.current ? `${friend.sender.avatar}` : `${friend.receiver.avatar}`}
                        content={friend.content}
                      />
              </li>
            ))}

          </ul>
        </div>
      </div>
    </div>
  )
}
