import React, { useState, useEffect } from "react";
import { getPost } from "../adapters/post";
import Post from "./Post";
import io from "socket.io-client";

const socket = io("http://127.0.0.1:4000", { transports: ["websocket"] });

export default function PostList() {
    const [post, setPost] = useState([]);
    const [hasReceivedSocketEvent, setHasReceivedSocketEvent] = useState(false);
    useEffect(() => {
        // Nhận thông báo từ máy chủ
        socket.on("postAdded", (newPost) => {
            // Khi có bài viết mới, cập nhật danh sách bài viết
            setPost(newPost);
            setHasReceivedSocketEvent(true);

            console.log(hasReceivedSocketEvent)
            console.log(post)
        });

        if (!hasReceivedSocketEvent) {
            update();
        }

    }, []);

    async function update() {
        const posts = await getPost();
        setPost(posts);
    }

    const handleNewPost = async (newPost) => {
        // Gửi thông điệp tới máy chủ khi có bài viết mới
        socket.emit("newPost", newPost);
    }

    return (
        <>
            {
                post.map((item, index) => (
                    <Post
                        key={index}
                        user={{
                            name: item.sender,
                            image: "https://antimatter.vn/wp-content/uploads/2022/10/hinh-anh-gai-xinh-de-thuong-457x600.jpg",
                        }}
                        content={{
                            time: "12 hours ago",
                            image: item.content.image !== "" ? `http://127.0.0.1:3001/uploads/${item.content.image}` : "",
                        }}
                        creationTime={item.creationTime}
                    />
                ))
            }
            {/* Thêm giao diện để tạo bài viết mới và gọi handleNewPost khi bài viết được tạo */}
        </>
    );
}
