import React, { useState, useEffect } from "react";
import Post from "./Post";
import io from "socket.io-client";
import InfiniteScroll from "react-infinite-scroll-component";

const server =
    process.env.REACT_APP_SEVER_IP + ":" +
    process.env.REACT_APP_SOCKET

const socket = io(server, { transports: ["websocket"] });

export default function PostList() {
    const [fromPage, setFromPage] = useState(1)
    const [limitPost, setLimitPost] = useState(4)
    const [post, setPost] = useState([]);
    const contentRef = useRef(null);
    const [isScrolledToBottom, setIsScrolledToBottom] = useState(false);
  
    useEffect(() => {
      const contentElement = contentRef.current;
  
      if (contentElement) {
        const observer = new IntersectionObserver(entries => {
          entries.forEach(entry => {
            if (entry.isIntersecting) {
              setIsScrolledToBottom(true);
            } else {
              setIsScrolledToBottom(false);
            }
          });
        });
  
        observer.observe(contentElement);
  
        return () => {
          observer.unobserve(contentElement);
        };
      }
    }, []);
    // LOAD POST ONE TIME
    useEffect(() => {
        fetchData(fromPage, limitPost)
    }, []);
    

    function fetchData(fromPage, toPage)
    {
        // Gửi thông báo đến server để lấy bài viết
        socket.emit("getpost", fromPage, toPage);
       
        socket.on("getpost", function(post) {
            setPost(post)
        })
    }

    function fetchMoreData() {
        const nextPost = limitPost + 3
            
        // Lấy thêm dữ liệu
        const data = fetchData(limitPost, nextPost)
        const newData = post.concat(data)
        setPost(newData)

        // Tăng giới hạn bài viết thêm + 3
        setLimitPost(nextPost)
    }

    return (
        <div id="A" ref={contentRef}>
            <InfiniteScroll
            dataLength={post.length}
            next={fetchMoreData}
            hasMore={true}
            loader={<h4>Loading...</h4>}
        >
            {
                post.map((item, index) => (
                    <Post
                        key={index}

                        _id={item._id}

                        sender={item.sender}

                        content={{
                            text: item.content.text,
                            image: item.content.image !== "" ? `http://127.0.0.1:3001/uploads/${item.content.image}` : "",
                        }}

                        creationTime={item.creationTime}

                        comments={item.comments}
                    />
                ))
            }
        </InfiniteScroll>
        </div>
    );
}
