import React, { useState, useEffect } from "react";
import Post from "./Post";
import io from "socket.io-client";
import InfiniteScroll from "react-infinite-scroll-component";
import { getLimitPost } from "../adapters/post";

const server =
    process.env.REACT_APP_SEVER_IP + ":" +
    process.env.REACT_APP_SOCKET

const socket = io(server, { transports: ["websocket"] });

export default function PostList() {
    const [fromPage, setFromPage] = useState(1)
    const [limitPost, setLimitPost] = useState(2)
    const [post, setPost] = useState([]);
    const [hasMore, setHasMore] = useState(true)

    // LOAD POST ONE TIME
    useEffect(() => {
        fetchData(fromPage, limitPost)

    }, []);

    function fetchData(fromPage, toPage)
    {
        // Gửi thông báo đến server để lấy bài viết
        socket.emit("getpost", fromPage, toPage);
       
        socket.on("getpost", function(post) {
            setPost(post)
        })
    }

    async function fetchMoreData() {
        if (post.length >= 16)
        {
            setHasMore(false)
            return
        }

        console.log(fromPage, limitPost)

        const nextPost = limitPost + 2
            
        // Lấy thêm dữ liệu
        const data = await getLimitPost(limitPost, nextPost);
        const newData = post.concat(data)
        setPost(newData)

        // Tăng giới hạn bài viết thêm
        setLimitPost(post.length)
    }

    return (
        <InfiniteScroll
            dataLength={post.length}
            next={fetchMoreData}
            hasMore={hasMore}
            loader={<h4>Loading...</h4>}
            height={500}
        >
            {
                post.map((item, index) => (
                    <Post
                        key={index}

                        _id={item._id}

                        sender={item.sender}

                        content={{
                            text: item.content.text,
                            image: item.content.image !== "" ? `http://127.0.0.1:3001/uploads/${item.content.image}` : "",
                        }}

                        creationTime={item.creationTime}

                        comments={item.comments}
                    />
                ))
            }
        </InfiniteScroll>
    );
}
