import FriendCard from './FriendCard'
import SearchBar from './SearchBar'
import { useEffect, useState, useRef } from 'react'
import socket from '../socket/socket-client'
import chatStyle from '../styles/chat.css'

export default function ListChat({ setSelectedFriend }) {
  const [listChat, setListChat] = useState([])

  const user = useRef()

  useEffect(() => {
    const session = JSON.parse(sessionStorage.getItem("userAuth"))
    user.current = session.user._id
    
    socket.emit("getListChat", user.current)

    function onListChat(listChat) {
      setListChat(listChat)

     // setSelectedFriend(listChat[0]) // Lấy user đầu tiên làm mặc định
      console.log(listChat)
    }

    socket.on("getListChat", onListChat)

    return () => {
      socket.off('getlistChat', onListChat);
    };
  }, [])

  const handleFriendClick = (friend) => {
    setSelectedFriend(friend);
  }

  return (
    <div id="right-side-bar">
      <SearchBar placeholder="Search anything" />

      <div id='list-listChat' className='wrap-list-item'>
        <div className="mt-25">
          <b className="heading ml-13">listChat</b>
          <ul className="list-item">
            {
            listChat.map((chat, index) => (
              <li key={index} onClick={() => handleFriendClick(chat)} className="row justify-content-space-between">
               <FriendCard
                  
                name={chat.recipients[0]._id === user.current  ? `${chat.recipients[1].firstName} ${chat.recipients[1].lastName}` : `${chat.recipients[0].firstName} ${chat.recipients[0].lastName}`}
                  
                unreadMessage={2}
                  
                avatar={chat.recipients[0]._id === user.current  ? `${chat.recipients[1].avatar}` : `${chat.recipients[0].avatar}`}
                  
                content={chat.messages[chat.messages.length - 1].content}
                  
                />
              </li>
            ))}

          </ul>
        </div>
      </div>
    </div>
  )
}
