import Post from './Post'
import {getPost} from '../adapters/post'
import { useState, useEffect } from 'react';

export default function PostList() {
    const [post, setPost] = useState([]);
    useEffect(() => {
        // Sử dụng useEffect để gọi hàm lấy bài viết và gán dữ liệu cho biến post khi component được tạo
        async function fetchPosts() {
            const posts = await getPost();
            setPost(post);
        }

        fetchPosts();
    }, []);

    post.map((item) => (
        <Post
            user={{
                name: "Đỗ Mộc Lan",
                image: "https://antimatter.vn/wp-content/uploads/2022/10/hinh-anh-gai-xinh-de-thuong-457x600.jpg"
            }}
            content={{
                time: "12 hours ago",
                image: `http://127.0.0.1:3001/uploads/${item.content.image}`
            }}
        />
    ))
}
