const importedStyles = {};

export const importStyles = (styleNames) => {
  styleNames.forEach((styleName) => {
    if (!importedStyles[styleName]) {
      importedStyles[styleName] = true;
      const link = document.createElement('link');
      // Chuyển đổi đường dẫn tương đối thành đường dẫn tuyệt đối
      const absoluteURL = new URL(`${styleName}`, window.location.origin).href;
      link.href = absoluteURL;
      link.rel = 'stylesheet';
      document.head.appendChild(link);
    }
  });
};

export const removeStyles = (styleNames) => {
  styleNames.forEach((styleName) => {
    if (importedStyles[styleName]) {
      const link = document.querySelector(`link[href*="${styleName}"]`);
      if (link) {
        document.head.removeChild(link);
        delete importedStyles[styleName];
      }
    }
  });
};
