import sendImageToServer from '../adapters/sendImageToServer'
import {sendPost} from '../adapters/post'
import React, { useEffect, useState } from 'react';
import { getUser } from '../adapters/user';

export default function HandlePost() {
    const [isValidContent, setIsValidContent] = useState(false)
    const [postContent, setPostContent] = useState("")
    const [imageDataURL, setImage] = useState("")
    const [fileStorage, setFile] = useState(null)
    let fileInputRef = React.createRef()

    useEffect(() => {
        if (postContent !== "" || imageDataURL !== "")
        {
            setIsValidContent(true)
        } else {
            setIsValidContent(false)
        }
    })

    function OpenPicture() {
        fileInputRef.current.click()
    }

    function handleImageChange(event) {
        const file = event.target.files[0]

        if (file) {
            const reader = new FileReader()

            reader.onload = () => {
                // Set image for class preview-image
                setImage(reader.result)
                // Save file to send server
                setFile(file)
            };
            // Read blob or file convert to base64 image
            reader.readAsDataURL(file)
        }
    };

    function handleMessageChange(e)
    {
        setPostContent(e.target.value)
    }

    /* UPLOAD POST */
    async function handleUpload() {
        if (isValidContent)
        {
            var storage = JSON.parse(sessionStorage.getItem("userAuth"));
            const userID = storage.user._id

            const data = {
                "sender": userID,
                "content": {
                  "text": postContent,
                  "image": fileStorage
                },
                "creationTime": new Date(),
                "displayMode": "public"
              }

            sendPost(data)


        }
    }
    /*------------------------------------------------------------------------- */
    return (
        <div className="handle-post bg-white pad-10">
            <div>
                <img
                    className="avatar"
                    src="https://taimienphi.vn/tmp/cf/aut/anh-gai-xinh-1.jpg"
                    alt="avatar"
                />
                <textarea
                    id="post-content"
                    className="ml-13"
                    placeholder="What's on your mind?"
                    name=""
                    value={postContent}
                    onChange={handleMessageChange}
                />
            </div>
            {/* PREVIEW PICTURE OR VIDEO */}
            <div id="js-review" className="mt-25">
                <img className="photo" src={imageDataURL}></img>
                {/* <div class="group-3">
<img class="photo"
  src="https://images.pexels.com/photos/4321836/pexels-photo-4321836.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
  alt="">
<img class="photo"
  src="https://images.pexels.com/photos/4651199/pexels-photo-4651199.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
  alt="">
<img class="photo"
  src="https://images.pexels.com/photos/4651067/pexels-photo-4651067.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1"
  alt="">
</div> */}
            </div>
            <div className="row mt-15 space-between">
                <ul className="list-icon row">
                    <li>
                        <img
                            id="js-upload-picture"
                            className="icon-heading"
                            src="https://img.icons8.com/pulsar-color/48/pictures-folder.png"
                            alt="pictures-folder"
                            onClick={OpenPicture}
                        />

                        <input
                            type="file"
                            accept="image/*"
                            style={{ display: 'none' }}
                            onChange={handleImageChange}
                            ref={fileInputRef}
                            multiple
                        />
                    </li>
                    <li>
                        <img
                            className="icon-heading"
                            src="https://img.icons8.com/cotton/64/docket--v1.png"
                            alt="docket--v1"
                        />
                    </li>
                    <li>
                        <img
                            className="icon-heading"
                            src="https://img.icons8.com/pulsar-color/48/pictures-folder.png"
                            alt="pictures-folder"
                        />
                    </li>
                    <li>
                        <img
                            className="icon-heading"
                            src="https://img.icons8.com/pulsar-color/48/pictures-folder.png"
                            alt="pictures-folder"
                        />
                    </li>
                </ul>
                <div onClick={handleUpload} id="js-btn-post" className={`btn ${
                    (isValidContent) ? 'btn-blue' : 'btn-disable'
                }`}>
                    Post
                </div>
            </div>
        </div>
    )
}
