import express from 'express'
const app = express()
const PORT = process.env.REACT_APP_UPLOAD_API_PORT
const PATH = process.env.REACT_APP_API_POST
const URL = `${process.env.REACT_APP_SEVER_IP}:${PORT}${PATH}`

app.use(express.json()); 

function getPost()
{
    app.post('/post', (req, res) => {
        const receivedData = req.body;
          console.log(receivedData)
        res.json({ message: 'Dữ liệu đã được nhận thành công' });
      });
      
      app.listen(3001, () => {
        console.log(`Server đang lắng nghe tại http://localhost:3001`);
      });
}

export default getPost
