// sendImageToServer.js

function sendImageToServer(url, imageData) {
    return fetch(url, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json', // You can adjust the content type as needed
        },
        body: JSON.stringify({ imageData }), // Send the image data as JSON or adjust as needed
    })
    .then(response => {
        if (!response.ok) {
            throw new Error('Image upload failed');
        }
        return response.json(); // You can parse the server response if needed
    });
}

export default sendImageToServer;
