// Trong file store.js
import { configureStore } from "@reduxjs/toolkit";
import friendReducer from "../src/redux/friendSlice"; // Tạo friendSlice

const store = configureStore({
  reducer: {
    friends: friendReducer, // Thêm reducer của friendSlice vào store
    // Thêm các reducer khác nếu cần
  },
});

export default store;
