import React, { useEffect, useState, Suspense } from 'react';
import HandlePost from '../components/HandlePost';
import NavigationBar from '../components/NavigationBar';
import Header from '../components/Header';
import FriendCard from '../components/FriendCard';
import SearchBar from '../components/SearchBar';


export default function HomePage() {
    useEffect(() => {
        import('../styles/homepage.css')
        const PostList = React.lazy(() => import('../components/PostList'));
    }, [])

    return (
        <div id="app">
            <NavigationBar />
            {/* WRAPPER */}
            <div>
                {/* HEADER */}
                <Header title="Homepage" />
                {/* WRAP CONTENT */}
                <div className="row">
                    <div id="content">
                        {/* HANDLE POST */}
                        <HandlePost />

                        {/* NƠI ĐẶT POST DO NGƯỜI DÙNG ĐĂNG */}
                        <Suspense fallback={<div>Loading...</div>}>
                            <PostList />
                        </Suspense>
                     
                        {/* --------------------------------------- */}
                    </div>
                    {/* RIGHT SIDEBAR */}
                    <div id="right-side-bar">
                        <SearchBar placeholder="Search anything" />
                        
                        <div className="mt-25">
                            <b className="heading ml-13">Friends</b>
                            <ul className="list-item">
                               <FriendCard name="Hoàng Ngân" unreadMessage="23" />
                               <FriendCard name="Hoàng Ngân" unreadMessage="23"/>
                               <FriendCard name="Hoàng Ngân" unreadMessage="23"/>
                               <FriendCard name="Hoàng Ngân" unreadMessage="23"/>
                            </ul>
                        </div>
                        <div className="mt-25">
                            <b className="heading ml-13">Friends</b>
                            <ul className="list-item">
                               <FriendCard name="Hoàng Ngân" unreadMessage="23" />
                               <FriendCard name="Hoàng Ngân" unreadMessage="23"/>
                               <FriendCard name="Hoàng Ngân" unreadMessage="23"/>
                               <FriendCard name="Hoàng Ngân" unreadMessage="23"/>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
