import React, { useEffect, useState } from 'react';

function Register() {
    const [form, setForm] = useState(
        {
            firstName: "",
            lastName: "",
            username: "",
            password: "",
            confirmPassword: ""
        }
    )
     const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [isValid, setIsValid] = useState(false);

    useEffect(() => {
        import('../styles/login.css')
    }, [])

    function handleInputChange(e) {
        const { id, value } = e.target;

        if (id === "firstName") {
            setFirstName(value);
        }
        if (id === "lastName") {
            setLastName(value);
        }
        if (id === "username") {
            setUsername(value)
        }
        if (id === "password") {
            setPassword(value);
        }

        if (firstName === "" || lastName === "" || username === "")
            setIsValid(false)
        
        if (id === "confirmPassword" && value === password) 
        {
            setIsValid(true);
        } 
        else
        {
            setIsValid(false)
        }

        if (id === "confirmPassword" && value === password) 
        {
            setIsValid(true);
        } 
    }

    function handleSubmit(e) {
        e.preventDefault();
        
        const registerInfo = {
            'firstName': firstName,
            'lastName': lastName,
            'username': username,
            'password': password
        }
        console.log('Form submitted', registerInfo);    
    }

    return (
        <div className="wrapper">
            <form onSubmit = {handleSubmit}>
                <h1>Register</h1>
                <div className="input-box row">
                    <input id='firstName'
                        className='mr-15'
                        type="text"
                        placeholder="First Name"
                        value={firstName}
                        onChange={(e) => handleInputChange(e)}
                    />
                    <input id='lastName'
                        type="text"
                        placeholder="Last Name"
                        value={lastName}
                        onChange={(e) => handleInputChange(e)}
                    />
                </div>
                <div className="input-box">
                    <input id='username'
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => handleInputChange(e)}
                    />
                    <span id="img-user" className="text-white material-symbols-outlined">
                        person
                    </span>
                </div>
                <div className="input-box">
                    <input id="password"
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => handleInputChange(e)}
                    />
                    <span id="img-lock" className="text-white material-symbols-outlined">
                        key
                    </span>
                </div>
                <div className="input-box">
                    <input
                        id="confirmPassword"
                        type="password"
                        placeholder="Confirm Password"
                        onChange={(e) => handleInputChange(e)}
                    />
                    <span id="img-lock" className="text-white material-symbols-outlined">
                        key
                    </span>
                </div>
                <button type="submit" className={
                    (isValid) ? 'btn' : 'btn btn-disable'
                }>
                    Register
                </button>
            </form>
        </div>
    )
}

export default Register;
