import { useEffect, useState } from 'react'
import CommentList from './CommentList'
import FormComment from './FormComment';

import postStyle from '../styles/post.css'

// Parameter:
// comments: array object
export default function Post({ _id, sender, content, creationTime, emoji, comments }) {
    const [user, setUser] = useState("")
    const [existImage, setExistImage] = useState(false)
    const [isActiveTooltip, setIsActiveTooltip] = useState(false)

    useEffect(() => {
        const user = (JSON.parse(sessionStorage.getItem("userAuth"))).user
        setUser(user)

        if (content.image !== "") {
            setExistImage(true)
        }

        const handleDocumentClick = (event) => {
            if (isActiveTooltip && !tooltipRef.current.contains(event.target)) {
                setIsActiveTooltip(false);
            }
        };

        // Lắng nghe sự kiện click trên toàn bộ tài liệu
        document.addEventListener('click', handleDocumentClick);

        return () => {
            document.removeEventListener('click', handleDocumentClick);
        };
    }, [])

    function activeTooltips()
    {
        setIsActiveTooltip(true);
    }

    return (
        <div className="post">
            <div className="row justify-between">
                {/* LEFT */}
                <div className="row align-items">
                    <img
                        className="avatar"
                        src={`http://127.0.0.1:3001/uploads/${sender.avatar}`}
                        alt=""
                    />
                    <div className="ml-10">
                        <p className="user">{sender.firstName + " " + sender.lastName}</p>
                        <p className="time">{creationTime}</p>
                    </div>
                </div>
                {/* RIGHT */}
                <ul className="list-icon row">
                    <li>
                        <i className="fa-solid fa-bookmark" />
                    </li>
                    <li className='more' onClick={activeTooltips}>
                        <i className="fa-solid fa-ellipsis-vertical" />

                        {/* MORE OPTIONS */}
                        <ul className= {(isActiveTooltip) ? 'tooltips tooltips-active' : 'tooltips'}>
                            <li><span className="material-symbols-outlined">delete</span>Delete This Post</li>
                        </ul>
                    </li>
                </ul>
            </div>
            <p className="text">
                {content.text}
            </p>
            <div className={`list-image ${existImage ? 'box-active' : 'box-disable'}`}>
                <img
                    className="photo"
                    src={content.image}
                    alt=""
                />
            </div>
            {/* Emoji */}
            <div className="emoji">
                <div>
                    <i className="fa-regular fa-heart" />
                    <span>Heart</span>
                </div>
                <div>
                    <i className="fa-regular fa-comment" />
                    <span>Comment</span>
                </div>
                <div>
                    <i className="fa-solid fa-share" />
                    <span>Share</span>
                </div>
            </div>
            {/* Form Comment */}
            <FormComment sender={user} postID={_id} />

            {/* Group Comment */}
            <CommentList comments={comments} />
        </div>
    )
}
