import { useState, useEffect } from 'react';
import socketIOClient from 'socket.io-client';

import { getPost, createPost } from '../adapters/post'; // Thay thế createPost bằng hàm tạo bài viết thực tế của bạn.
import Post from './Post';

export default function PostList() {
    const [posts, setPosts] = useState([]);
    const socket = socketIOClient('http://127.0.0.1:3001'); // Thay thế URL WebSocket bằng URL thích hợp.

    useEffect(() => {
        // Lắng nghe sự kiện mới từ WebSocket và cập nhật danh sách bài viết.
        socket.on('newPost', (newPost) => {
            setPosts((prevPosts) => [newPost, ...prevPosts]);
        });

        // Gọi hàm getPost ban đầu để tải danh sách bài viết.
        async function fetchPosts() {
            const postList = await getPost();
            setPosts(postList);
        }

        fetchPosts(); // Gọi hàm để tải danh sách bài viết ban đầu.

        return () => {
            socket.disconnect();
        };
    }, []);

    // Hàm này được gọi khi bạn đăng bài viết mới.
    async function handleCreatePost(newPostData) {
        const newPost = await createPost(newPostData); // Thực hiện tạo bài viết mới
        socket.emit('newPost', newPost); // Gửi thông báo về bài viết mới đến máy chủ WebSocket
    }

    return (
        <>
            {posts.map((item, index) => (
                <Post
                    key={index}
                    user={{
                        name: item.sender,
                        image: "https://antimatter.vn/wp-content/uploads/2022/10/hinh-anh-gai-xinh-de-thuong-457x600.jpg"
                    }}
                    content={{
                        time: "12 hours ago",
                        image: `http://127.0.0.1:3001/uploads/${item.content.image}`
                    }}
                    creationTime={item.creationTime}
                />
            ))}
            <button onClick={() => handleCreatePost(newPostData)}>Đăng bài</button>
        </>
    );
}
