import { useEffect, useState } from "react";
import { addComment } from "../adapters/comment";

export default function FormComment({sender, postID}) {
    useEffect(() => {

    }, [])

    function postComment() {
        const commentDOM = document.querySelector('#comment-text');

        if (commentDOM) {
            const pValue = commentDOM.textContent;

            const comment = {
                'postID': postID,
                'content': pValue,
                'sender': sender._id
            }

            // Add comment
            addComment(comment)
        }
    }

    return (
        <div className="form-comment row">
            <img
                className="avatar"
                src={`http://127.0.0.1:3001/uploads/${sender.avatar}`}
                alt="avatar"
            />
            <div>
                {/* <p
                    data-placeholder="Điền comment của bạn tại đây"
                    contentEditable="true"
                    id='comment-text'
                    className="post-comment"
                    name=""></p> */}
                    <textarea></textarea>
                <div className="icon-comment">
                    <ul className="list-icon row">
                        <li>
                            <img
                                id="js-upload-picture"
                                className="icon-heading"
                                src="https://img.icons8.com/pulsar-color/48/pictures-folder.png"
                                alt="pictures-folder"
                            />
                        </li>
                        <li>
                            <img
                                className="icon-heading"
                                src="https://img.icons8.com/pulsar-color/48/pictures-folder.png"
                                alt="pictures-folder"
                            />
                        </li>
                    </ul>
                    <div id="js-btn-post" className="btn btn-blue" onClick={postComment}>
                        Post
                    </div>
                </div>
            </div>
        </div>
    )
}
