// Trong file friendSlice.js
import { createSlice } from "@reduxjs/toolkit";

const friendSlice = createSlice({
  name: "friends",
  initialState: {
    friends: [],
    selectedFriend: null,
  },
  reducers: {
    setFriends: (state, action) => {
      state.friends = action.payload;
    },
    selectFriend: (state, action) => {
      state.selectedFriend = action.payload;
    },
  },
});

export const { setFriends, selectFriend } = friendSlice.actions;

export const selectAllFriends = (state) => state.friends.friends;
export const selectSelectedFriend = (state) => state.friends.selectedFriend;

export default friendSlice.reducer;
