import React, { useEffect, useState } from 'react';
import CommentList from './CommentList';
import FormComment from './FormComment';
import { deletePost } from '../adapters/post';
import Resizer from 'react-image-file-resizer'; // Import the Resizer library
import postStyle from '../styles/post.css';

const server =
  process.env.REACT_APP_SEVER_IP + ':' + process.env.REACT_APP_SERVER_PORT;

export default function Post({
  _id,
  sender,
  content,
  creationTime,
  emoji,
  comments,
  post,
  setPost,
}) {
  const [user, setUser] = useState('');
  const [existImage, setExistImage] = useState(false);
  const [isActiveTooltip, setIsActiveTooltip] = useState(false);

  useEffect(() => {
    const user = JSON.parse(sessionStorage.getItem('userAuth')).user;
    setUser(user);
  }, []);

  useEffect(() => {
    if (content.image !== '') {
      setExistImage(true);
    } else {
      setExistImage(false);
    }
  }, [content.image]);

  function removeElementAt(arr, index) {
    if (index < 0 || index >= arr.length) {
      return arr.slice();
    }
    return arr.filter((_, i) => i !== index);
  }

  async function handleDeletePost() {
    for (let i = 0; i < post.length; i++) {
      if (post[i]._id === _id) {
        setPost(removeElementAt(post, i));
        break;
      }
    }
    await deletePost(_id);
  }

  function activeTooltips() {
    if (isActiveTooltip === false) setIsActiveTooltip(true);
    else setIsActiveTooltip(false);
  }

  const resizeImage = (image) => {
    Resizer.imageFileResizer(
      image,
      300, // New width
      300, // New height
      'JPEG', // Image format
      100, // Quality
      0, // Rotation
      (uri) => {
        // Callback function when resizing is done
        content.image = uri; // Update the image URI
        setExistImage(true);
      },
      'base64', // Output type
      300, // Max file size (in bytes)
      1500 // Max width/height after resizing
    );
  };

  // Call the resizeImage function if content.image exists
  if (content.image) {
    resizeImage(content.image);
  }

  return (
    <div className="post">
      <div className="row justify-between">
        {/* LEFT */}
        <div className="row align-items">
          <img
            className="avatar"
            src={`${server}/uploads/${sender.avatar}`}
            alt=""
          />

          <div className="ml-10">
            <p className="user">{sender.firstName + ' ' + sender.lastName}</p>
            <p className="time">{creationTime}</p>
          </div>
        </div>
        {/* RIGHT */}
        <ul className="list-icon row">
          <li>
            <i className="fa-solid fa-bookmark" />
          </li>
          <li className="more" onClick={activeTooltips}>
            <i className="fa-solid fa-ellipsis-vertical" />

            {/* MORE OPTIONS */}
            <ul className={isActiveTooltip ? 'tooltips tooltips-active' : 'tooltips'}>
              <li
                className={sender._id !== user._id ? 'disable' : ''}
                onClick={handleDeletePost}
              >
                <span className="material-symbols-outlined">delete</span>Delete This Post
              </li>

              <li>
                <span className="material-symbols-outlined">delete</span>Add Bookmark
              </li>
            </ul>
          </li>
        </ul>
      </div>
      <p className="text">{content.text}</p>
      <div className={`list-image ${existImage ? 'box-active' : 'box-disable'}`}>
        <div
          style={{
            backgroundImage: `url(${content.image})`,
          }}
          className="boxReview-image"
        ></div>
      </div>
      {/* Emoji */}
      <div className="emoji">
        <div>
          <i className="fa-regular fa-heart" />
          <span>Heart</span>
        </div>
        <div>
          <i className="fa-regular fa-comment" />
          <span>Comment</span>
        </div>
        <div>
          <i className="fa-solid fa-share" />
          <span>Share</span>
        </div>
      </div>
      {/* Form Comment */}
      <FormComment sender={user} postID={_id} />

      {/* Group Comment */}
      <CommentList comments={comments} />
    </div>
  );
}
