import FriendCard from './FriendCard';
import SearchBar from './SearchBar';
import listFriendsStyle from '../styles/listFriends.css';
import { useEffect, useState } from 'react';

import { useSelector, useDispatch } from "react-redux";
import { selectAllFriends, selectFriend } from "./friendSlice";

export default function ListFriends({ friends, isListChat, setSelectedFriend }) {
  useEffect(() => {
    // Your useEffect code here
  }, []);

  return (
    <div id="right-side-bar">
      <SearchBar placeholder="Search anything" />

      <div id='list-friends' className='wrap-list-item'>
        <div className="mt-25">
          <b className="heading ml-13">Friends</b>
          <ul className="list-item">
            {friends.map((friend, index) => (
              <FriendCard
                key={index}
                name={friend.receiver.firstName + " " + friend.receiver.lastName}
                unreadMessage={friend.unreadMessage}
                avatar={friend.receiver.avatar}
                content={isListChat ? friend.content : undefined}
              />
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
