import { useEffect, useState } from "react";
import CommentCard from "./CommentCard";

// comments: array object from 'Comment' collection

/* Ghi {comment} khác với comments bởi vì:  
    Nếu khi comments thì nó sẽ trả về dạng object chứa thuộc tính comments và các thuộc tính khác
    cho nên ghi {comment} là để truy cập vào thuộc tính comments
*/

import io from "socket.io-client";

export default function CommentList({comments}) {
    return (
        <div className="group-comment">
            {
                comments.map((item, index) => (
                    <CommentCard key={index} comment={item} />
                ))
            }
        </div>
    )
}
