import messageStyle from '../styles/message.css'

/*
message = 
{
  "_id": {
    "$oid": "652bbc19a26b30172fa34f59"
  },
  "sender": {
    "$oid": "65253691fc11231d37f45a3a"
  },
  "recipients": {
    "$oid": "652a1ec8d0960eece31bc32d"
  },
  "timestamp": "Sat Oct 14 2023 07:14:01 GMT+0700 (Indochina Time)",
  "content": "Đi chơi không ?"
}
*/
export default function Message({position, message}) {

    return 
    (
        <div className="comment comment-right">
        <div className="box">
            <b className="sender">{message.sender.firstName + " " + message.sender.lastName}</b>
            <p className="text">{message.content}</p>
        </div>
        <img
            className="avatar"
            src={`http://127.0.0.1:3001/uploads/${message.sender.avatar}`}
            alt="avatar"
        />
    </div>
    )
}
