const importedScripts = {};

export const importScript = (scriptNames) => {
  scriptNames.forEach((scriptName) => {
    if (!importedScripts[scriptName]) {
      importedScripts[scriptName] = true;
      const script = document.createElement('script');
      const absoluteURL = new URL(`./scripts/${scriptName}`, window.location.origin).href;
      script.src = absoluteURL;
      document.body.appendChild(script);
    }
  });
};

export const removeScript = (scriptNames) => {
  scriptNames.forEach((scriptName) => {
    if (importedScripts[scriptName]) {
      const script = document.querySelector(`script[src*="${scriptName}"]`);
      if (script) {
        document.body.removeChild(script);
        delete importedScripts[scriptName];
      }
    }
  });
};
