import React, { useState, useEffect } from "react";
import Post from "./Post";
import io from "socket.io-client";
import InfiniteScroll from "react-infinite-scroll-component";
import { getLimitPost, getNumberPost } from "../adapters/post";

const server =
    process.env.REACT_APP_SEVER_IP + ":" +
    process.env.REACT_APP_SERVER_SOCKET

const socket = io(server, { transports: ["websocket"] });

export default function PostList() {
    const [maxPost, setMaxPost] = useState(0)
    const [fromPage, setFromPage] = useState(1)
    const [limitPost, setLimitPost] = useState(2)
    const [post, setPost] = useState([]);
    const [hasMore, setHasMore] = useState(false)

    // LOAD POST ONE TIME

    useEffect(() => {
        fetchData(fromPage - 1, limitPost);
        
        getMaxPost();

        socket.on("postAdded", (newPost) => {
            const newList = [newPost, ...post];
            setPost(newList);
        });

        socket.on("commentAdded", (commentData) => {
            // Tìm bài viết cần cập nhật
            const updatedPosts = post.map((item) => {
                if (item._id === commentData.postId) {
                    // Tạo một bản sao của bài viết và cập nhật danh sách bình luận
                    return {
                        ...item,
                        comments: [commentData.comment, ...item.comments],
                    };
                }
                return item;
            });
        
            setPost(updatedPosts);
        });
        
    
        return () => {
            socket.off("postAdded");
            socket.off("commentAdded");
        }
    }, [])

    async function getMaxPost()
    {
        const MAXPOST = await getNumberPost()

        setMaxPost(MAXPOST.numberPost)

        if (MAXPOST.numberPost > 0)
            setHasMore(true)
    }

    function fetchData(fromPage, toPage)
    {
        // Gửi thông báo đến server để lấy bài viết
        socket.emit("getpost", fromPage, toPage);
       
        socket.on("getpost", function(post) {
            setPost(post)
        })
    }

    async function fetchMoreData() {
        if (post.length === maxPost)
        {
            setHasMore(false)
            return
        }

        setTimeout(async () => {
            const toPost = post.length + limitPost
            
            // Lấy thêm dữ liệu
            const data = await getLimitPost(post.length, toPost);
            const newData = post.concat(data)

            setPost(newData)

        }, (900));  
        /* Độ trễ phải từ 900 trở lên để server kịp xử lý, 
        // xảy ra khi infinite scroll hoạt động sẽ lấy dữ liệu từ db
        Nếu tốc độ scroll nhanh hơn dẫn tới việc dữ liệu vừa được xóa xong (client) 
        vừa cập nhật xong thì infinite scroll gọi lại
        */
    }

    return (
        <InfiniteScroll
            dataLength={post.length}
            next={fetchMoreData}
            hasMore={hasMore}
            loader={<h4>Loading...</h4>}
            scrollableTarget="content"
        >
            {
                post.map((item, index) => (
                    <Post
                        key={index}

                        _id={item._id}

                        sender={item.sender}

                        content={{
                            text: item.content.text,
                            image: item.content.image !== "" ? `http://127.0.0.1:3001/uploads/${item.content.image}` : "",
                        }}

                        creationTime={item.creationTime}

                        comments={item.comments}

                        post={post}

                        setPost={setPost}
                    />
                ))
            }
        </InfiniteScroll>
    );
}
