import { useEffect, useRef, useState } from "react";
import Headers from '../components/Header'
import ListFriends from '../components/ListFriends'
import TextareaAutosize from 'react-textarea-autosize';
import socket from "../socket/socket-client";
import Message from "../components/Message";
import chatStyle from '../styles/chat.css';
import { useDispatch, useSelector } from 'react-redux';
import { setFriends, selectFriend } from '../redux/reducers/chatReducer';

export default function Chat() {
    const [message, setMessage] = useState("");
    const user = useRef();
    const dispatch = useDispatch();
    const selectedFriend = useSelector(state => state.chat.selectedFriend);

    useEffect(() => {
        import('../styles/homepage.css');
        import('../styles/general.css');

        const session = JSON.parse(sessionStorage.getItem("userAuth"));
        user.current = session.user._id;

        socket.emit("getFriends", user.current, (friends) => {
            dispatch(setFriends(friends));
          });
       
        // Nếu không có thì .. ngược lại sẽ lấy friend đầu tiên làm mặc định

    }, []);

    // ...
}
