import React, { useEffect, useState, useRef } from 'react';
import Headers from '../components/Header';
import ListFriends from '../components/ListFriends';
import TextareaAutosize from 'react-textarea-autosize';
import socket from '../socket/socket-client';
import Message from '../components/Message';
import chatStyle from '../styles/chat.css';
import { useDispatch, useSelector } from 'react-redux';
import { setFriends, selectFriend } from './chatReducer';

function Chat() {
    const [message, setMessage] = useState("");
    const user = useRef();
    const dispatch = useDispatch();
    const selectedFriend = useSelector(state => state.chat.selectedFriend);
    const friends = useSelector(state => state.chat.friends);

    useEffect(() => {
        import('../styles/homepage.css');
        import('../styles/general.css');

        const session = JSON.parse(sessionStorage.getItem("userAuth"));
        user.current = session.user._id;

        socket.emit("getFriends", user.current, (friends) => {
            dispatch(setFriends(friends));
        });

        // Nếu không có thì .. ngược lại sẽ lấy friend đầu tiên làm mặc định
    }, []);

    function sendMessage() {
        const data = {
            idSender: user.current.id,
            idRecipient: selectedFriend.id,
            content: message
        };

        socket.emit("sendMessage", data);
    }

    return (
        <div id="app">
            <Headers customStyle='header-chat' title="Message" />

            <div id="wrap-message" className="row">
                <ListFriends friends={friends} isListChat={true} />

                {/* BOX CHAT */}
                <div className="parent flex-1 mt-15">
                    <div id="boxChat" className="row justify-space-between mlr-32">

                        {/* AVATAR AND NAME */}
                        <div className="row align-items-center">
                            <img
                                className="avatar"
                                src={selectedFriend ? selectedFriend.avatar : ''} // Sử dụng selectedFriend từ Redux
                                alt="avatar"
                            />

                            <div className="ml-15">
                                <h3>{selectedFriend ? selectedFriend.name : ''}</h3> {/* Sử dụng selectedFriend từ Redux */}
                                <p>Nhìn gì cha</p>
                            </div>
                        </div>

                        {/* LIST ICON */}
                        <div className="row">
                            <a className="icon-effect" href="#">
                                <i className="fa-regular fa-envelope" />
                            </a>
                            <a className="icon-effect" href="#">
                                <i className="fa-solid fa-gears" />
                            </a>
                            <a className="icon-effect" href="#">
                                <i className="fa-regular fa-heart" />
                            </a>
                        </div>
                    </div>

                    {/* VIEW CHAT */}
                    <div id="viewchat" className="mlr-32">
                    </div>

                    <div id="boxMessage" className="row">
                        <TextareaAutosize
                            className="post-comment ml-13"
                            placeholder="Bạn đang nghĩ gì ?"
                            onChange={e => setMessage(e.target.value)} />

                        <img onClick={sendMessage} id="btn" width="36" height="36" src="https://img.icons8.com/sf-regular/48/paper-plane.png" alt="paper-plane" />
                    </div>

                </div>
            </div>
        </div>
    );
}

export default Chat;
