// Trong friendSlice.js
import { createSlice } from "@reduxjs/toolkit";

const friendSlice = createSlice({
  name: "friends",
  initialState: {
    friends: [], // Khởi tạo mảng friends trong initialState
    selectedFriend: null,
  },
  reducers: {
    setFriends: (state, action) => {
      state.friends = action.payload; // Cập nhật danh sách bạn bè
    },
    // Các reducers khác
  },
});

export const { setFriends, selectFriend } = friendSlice.actions;

// Các selectors và reducer khác
