import FriendCard from './FriendCard'
import SearchBar from './SearchBar'
import { useEffect, useState, useRef } from 'react'
import socket from '../socket/socket-client'
import listFriendsStyle from '../styles/listFriends.css'

export default function ListFriends({ isListChat, setSelectedFriend }) {
  const [friends, setFriends] = useState([])
  const user = useRef()

  useEffect(() => {
    const session = JSON.parse(sessionStorage.getItem("userAuth"))
    user.current = session.user._id

    socket.emit("getFriends", user.current)

    function onGetFriends(friends) {
      setFriends(friends)
    }
    socket.on("getFriends", onGetFriends)

    return () => {
      socket.off('getFriends', onGetFriends);
    };
  }, [])

  // Hàm xử lý khi nhấp vào một bạn bè
  const handleFriendClick = (friend) => {
    setSelectedFriend(friend);
  }

  return (
    <div id="right-side-bar">
      <SearchBar placeholder="Search anything" />

      <div id='list-friends' className='wrap-list-item'>
        <div className="mt-25">
          <b className="heading ml-13">Friends</b>
          <ul className="list-item">
            {friends.map((friend, index) => (
              <li key={index} onClick={() => handleFriendClick(friend)}>
                <FriendCard
                  name={friend.receiver.firstName + " " + friend.receiver.lastName}
                  unreadMessage={friend.unreadMessage}
                  avatar={friend.receiver.avatar}
                  content={isListChat ? friend.content : undefined}
                />
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  )
}
