import React, { useEffect, useRef, useState } from "react";
import Headers from '../components/Header';
import ListChat from "../components/ListChat";
import TextareaAutosize from 'react-textarea-autosize';
import socket from "../socket/socket-client";
import Message from "../components/Message";
import { useNavigate } from 'react-router-dom';

// PAGES
import NoFriend from './NoFriend'

// Import CSS styles
import chatStyle from '../styles/chat.css';
import homepageStyle from '../styles/homepage.css';
import generalStyle from '../styles/general.css';

export default function Chat() {
    const [server, setServer] = useState("");
    const [message, setMessage] = useState("");
    const [listChat, setListChat] = useState([]);
    const [selectedFriend, setSelectedFriend] = useState(null);
    const userRef = useRef();
    const navigate = useNavigate();
    const [isAccess, setIsAccess] = useState(true);

    useEffect(() => {
        const session = JSON.parse(sessionStorage.getItem("userAuth"));
        userRef.current = session.user;
        setServer(`${process.env.REACT_APP_SEVER_IP}:${process.env.REACT_APP_SERVER_PORT}`);

        if (!session.user.hasOwnProperty("friends")) {
            setIsAccess(false);
        }
    }, []);

    useEffect(() => {
        if (selectedFriend) {
            const recipients = [
                userRef.current._id,
                selectedFriend.recipients._id
            ];

            // Xóa bỏ sự kiện lắng nghe trước đó
            socket.off("getMessages", onGetMessages);

            // Thêm sự kiện lắng nghe mới
            socket.on("getMessages", onGetMessages);
            socket.emit("getMessages", recipients);
        }
    }, [selectedFriend]);

    function onGetMessages(newMessages) {
        if (Array.isArray(newMessages)) {
            // If newMessages is an array, merge it with the existing listChat
            setListChat(prevMessages => [...prevMessages, ...newMessages]);
        } else if (newMessages instanceof Object) {
            // If newMessages is a single message object, add it to the listChat
            setListChat(prevMessages => [...prevMessages, newMessages]);
        } else {
            // Handle other cases accordingly
            console.error("Received unexpected newMessages format:", newMessages);
        }
    }
    
    function sendMessage() {
        if (message.trim() === "") {
            return;
        }

        const data = {
            'sender': userRef.current,
            'content': message,
            'creationTime': new Date()
        }

        const recipients = [
            userRef.current._id,
            selectedFriend.recipients._id
        ]

        // Reset tin nhắn
        setMessage("");

        socket.emit("addMessage", data, recipients);
    }

    if (!isAccess) {
        return <NoFriend />;
    }

    return (
        <div id="app">
            <Headers customStyle='header-chat' title="Message" />

            <div id="wrap-message" className="row">
                <ListChat setSelectedFriend={setSelectedFriend} />

                {/* Chat Box */}
                <div className="parent flex-1 mt-15">
                    <div id="boxChat" className="row justify-space-between">

                        {/* Avatar and Name */}
                        <div className="row align-items-center">
                            <img
                                className="avatar"
                                src={(selectedFriend) ? `${server}/uploads/${selectedFriend.recipients.avatar}` : ""}
                                alt="avatar"
                            />

                            <div className="ml-15">
                                <h3>{(selectedFriend) ? selectedFriend.recipients.firstName + " " + selectedFriend.recipients.lastName : ""}</h3>
                                <p>Online</p>
                            </div>
                        </div>

                        {/* List Icons */}
                        <div className="row">
                            <a className="icon-effect" href="#">
                                <i className="fa-regular fa-envelope" />
                            </a>
                            <a className="icon-effect" href="#">
                                <i className="fa-solid fa-gears" />
                            </a>
                            <a className="icon-effect" href="#">
                                <i className="fa-solid fa-gears" />
                            </a>
                        </div>
                    </div>

                    {/* Chat Messages View */}
                    <div id="viewchat" >
                        {listChat.map((chat, index) => (
                            <Message
                                key={index}
                                owner={(userRef.current._id === chat.sender_id) ? "my" : "other"}
                                chat={chat}
                                selectedFriend={selectedFriend}
                                currentUser={userRef.current}
                            />
                        ))}
                    </div>

                    {/* Message Input Box */}
                    <div id="boxMessage" className="row">
                        <TextareaAutosize
                            className="post-comment"
                            placeholder="Enter a message"
                            onChange={e => setMessage(e.target.value)}
                            value={message}
                        />
                        <img onClick={sendMessage} id="btn" width="36" height="36" src="https://img.icons8.com/sf-regular/48/paper-plane.png" alt="paper-plane" />
                    </div>
                </div>
            </div>
        </div>
    )
}
