import React, { useState, useEffect } from "react";
import Post from "./Post";
import io from "socket.io-client";
import InfiniteScroll from "react-infinite-scroll-component";

const server =
    process.env.REACT_APP_SEVER_IP + ":" +
    process.env.REACT_APP_SOCKET

const socket = io(server, { transports: ["websocket"] });

export default function PostList() {
    const [fromPage, setFromPage] = useState(1)
    const [limitPost, setLimitPost] = useState(3)
    const [post, setPost] = useState([]);

    // Đăng bài 
    useEffect(() => {

        fetchFirstData()
        
    }, []);

    function fetchFirstData()
    {
        // Gửi thông báo đến server để lấy bài viết
        socket.emit("getpost", fromPage, limitPost);
       

        socket.on("getpost", function(post) {
            setPost(post)
        })
    }

    function fetchMoreData() {

    }

    return (
        <InfiniteScroll
            dataLength={post.length}
            next={fetchMoreData}
            hasMore={true}
            loader={<h4>Loading...</h4>}
        >
            {
                post.map((item, index) => (
                    <Post
                        key={index}

                        _id={item._id}

                        sender={item.sender}

                        content={{
                            text: item.content.text,
                            image: item.content.image !== "" ? `http://127.0.0.1:3001/uploads/${item.content.image}` : "",
                        }}

                        creationTime={item.creationTime}

                        comments={item.comments}
                    />
                ))
            }
        </InfiniteScroll>
    );
}
