import { useEffect, useRef, useState } from "react"
import generalStyle from '../styles/general.css'

export default function FriendCard({ name, unreadMessage, avatar, content }) {
    const [server, setServer] = useState("")

    useEffect(() => {
        setServer(process.env.REACT_APP_SEVER_IP + ":" + process.env.REACT_APP_SERVER_PORT)
    
        shortContent(content)

        console.log(content)
    }, [])

    function shortContent(content)
    {
        // Limit 15 character
        if (content.className > 15)
            content = content.slice(0, 15)
    }

    return (
        <>
          <div className="row">
                <div className="wrap-avatar">
                    <span className="activity-status activity-status-online" />
                    <img
                        className="avatar-small"
                        src={`${server}/uploads/${avatar}`}
                        alt="avatar"
                    />
                </div>

                {(!content) 
                    ?
                    <p className="flex align-items-center ml-8">{name}</p> 
                    : 
                    <div className="ml-15">
                        <p className="flex-1">{name}</p>
                        <span className='mt-5 color-red'>{content}</span>
                    </div>
                }

            </div>
            <span className="count-unread-message">2</span>
        </>
    )
}
