import { useEffect, useRef } from 'react'
import storiesStyle from '../styles/stories.css'

export default function Stories()
{
    const serverRef = useRef()

    useEffect(() => {
        serverRef.current = `${process.env.REACT_APP_SEVER_IP}:${process.env.REACT_APP_SERVER_PORT}`
    }, [])

    return (
        <div className='stories'>
            <ul className='list'>
                <li className='card' style={
                    {
                        backgroundImage: `url(${serverRef.current}/uploads/hero.jpg)`
                    }
                }>
                    <div className='info'>
                        <img className='avatar' src='https://yt3.googleusercontent.com/inhxgLbhHuXL6IllrpCH9jw7jdb0aQLv4hpVdATYsBGJAwFYs8OpuvBKnKz-8M2eHp1oXvoyIQ=s900-c-k-c0x00ffffff-no-rj'></img>
                        <p>Ngọc Ngân</p>
                    </div>
                </li>
                <li className='card' style={
                    {
                        backgroundImage: `url(${serverRef.current}/uploads/girl01.jpg)`
                    }
                }>
                    <div className='info'>
                        <img className='avatar' src='https://yt3.googleusercontent.com/inhxgLbhHuXL6IllrpCH9jw7jdb0aQLv4hpVdATYsBGJAwFYs8OpuvBKnKz-8M2eHp1oXvoyIQ=s900-c-k-c0x00ffffff-no-rj'></img>
                        <p>Ngọc Ngân</p>
                    </div>
                </li>
                <li className='card' style={
                    {
                        backgroundImage: `url(${serverRef.current}/uploads/gym-girl.jpg)`
                    }
                }>
                    <div className='info'>
                        <img className='avatar' src='https://yt3.googleusercontent.com/inhxgLbhHuXL6IllrpCH9jw7jdb0aQLv4hpVdATYsBGJAwFYs8OpuvBKnKz-8M2eHp1oXvoyIQ=s900-c-k-c0x00ffffff-no-rj'></img>
                        <p>Ngọc Ngân</p>
                    </div>
                </li>
                <li className='card'>
                    <div className='info'>
                        <img className='avatar' src='https://yt3.googleusercontent.com/inhxgLbhHuXL6IllrpCH9jw7jdb0aQLv4hpVdATYsBGJAwFYs8OpuvBKnKz-8M2eHp1oXvoyIQ=s900-c-k-c0x00ffffff-no-rj'></img>
                        <p>Ngọc Ngân</p>
                    </div>
                </li>
                <li className='card'>
                    <div className='info'>
                        <img className='avatar' src='https://yt3.googleusercontent.com/inhxgLbhHuXL6IllrpCH9jw7jdb0aQLv4hpVdATYsBGJAwFYs8OpuvBKnKz-8M2eHp1oXvoyIQ=s900-c-k-c0x00ffffff-no-rj'></img>
                        <p>Ngọc Ngân</p>
                    </div>
                </li>
            </ul>
        </div>
    )
}
