import React, { useEffect, useState, Suspense } from 'react';
import PostList from '../components/PostList';
import HandlePost from '../components/HandlePost';
import NavigationBar from '../components/NavigationBar';
import Header from '../components/Header';
import FriendCard from '../components/FriendCard';
import SearchBar from '../components/SearchBar';
import Stories from '../components/Stories';

export default function HomePage() {
    useEffect(() => {
        import('../styles/homepage.css')
        
    }, [])

    function Loading() {
        return <h2>游 Loading...</h2>;
      }

    return (
        <div id="app">
            <NavigationBar />
            {/* WRAPPER */}
            <div className='flex-1'>
                {/* HEADER */}
                <Header title="Homepage" />
                {/* WRAP CONTENT */}
                <div id='wrap-content-homepage' className="row">
                    <div id="content">
                        {/* STORIES */}
                        <Stories />
                        
                        {/* HANDLE POST */}
                        <HandlePost />

                        {/* NI 캟故욊 POST DO NG퀊敲I D칓NG 캟캑NG */}
                        <Suspense fallback={<Loading />}>
                            <PostList />
                        </Suspense>
                        
                        {/* --------------------------------------- */}
                    </div>
                    {/* RIGHT SIDEBAR */}
                    <div id="right-side-bar">
                        <SearchBar placeholder="Search anything" />
                        
                        <div className='wrap-list-item'>
                        <div className="mt-25">
                            <b className="heading ml-13">Friends</b>
                            <ul className="list-item">
                               <FriendCard name="Ho맕g Ng칙n" unreadMessage="23" />
                               <FriendCard name="Ho맕g Ng칙n" unreadMessage="23"/>
                               <FriendCard name="Ho맕g Ng칙n" unreadMessage="23"/>
                               <FriendCard name="Ho맕g Ng칙n" unreadMessage="23"/>
                            </ul>
                        </div>
                        <div className="mt-25">
                            <b className="heading ml-13">Friends</b>
                            <ul className="list-item">
                               <FriendCard name="Ho맕g Ng칙n" unreadMessage="23" />
                               <FriendCard name="Ho맕g Ng칙n" unreadMessage="23"/>
                               <FriendCard name="Ho맕g Ng칙n" unreadMessage="23"/>
                               <FriendCard name="Ho맕g Ng칙n" unreadMessage="23"/>
                            </ul>
                        </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
