import axios from 'axios';

const PORT = process.env.REACT_APP_PORT;
const PATH = process.env.REACT_APP_API_POST;
const URL = `${process.env.REACT_APP_SEVER_IP}:${PORT}${PATH}`;

async function getPost() {
    try {
        const response = await axios.get(URL);
        return response.data;
    } catch (error) {
        console.error('Lỗi khi lấy dữ liệu từ máy chủ:', error);
        throw error; // Re-throw lỗi để nó có thể được xử lý ở nơi gọi hàm.
    }
}

export default getPost
