import { useEffect, useState } from 'react';
import socketIOClient from 'socket.io-client';

import { getPost } from '../adapters/post';
import Post from './Post';

export default function PostList() {
    const [posts, setPosts] = useState([]);
    const socket = socketIOClient('http://your-server-url'); // Thay đổi URL WebSocket của bạn

    useEffect(() => {
        socket.on('newPost', (newPost) => {
            setPosts((prevPosts) => [newPost, ...prevPosts]);
        });

        return () => {
            socket.disconnect();
        };
    }, []);

    return (
        <>
            {posts.map((item, index) => (
                <Post
                    key={index}
                    user={{
                        name: item.sender,
                        image: "https://antimatter.vn/wp-content/uploads/2022/10/hinh-anh-gai-xinh-de-thuong-457x600.jpg"
                    }}
                    content={{
                        time: "12 hours ago",
                        image: `http://127.0.0.1:3001/uploads/${item.content.image}`
                    }}
                    creationTime={item.creationTime}
                />
            ))}
        </>
    );
}
