import React, { useState, useEffect } from "react";
import Post from "./Post";
import io from "socket.io-client";
import InfiniteScroll from "react-infinite-scroll-component";

const server =
    process.env.REACT_APP_SEVER_IP + ":" +
    process.env.REACT_APP_SOCKET;

const socket = io(server, { transports: ["websocket"] });

export default function PostList() {
    const [post, setPost] = useState([]);
    const [limitPost, setLimitPost] = useState([]);
    const [currentPage, setCurrentPage] = useState(1); // Thay đổi giá trị ban đầu thành 1
    const startPage = 1;

    // Đăng bài 
    useEffect(() => {
        // Gửi thông báo đến server để lấy bài viết
        socket.emit("getpost");

        const handleGetPost = (newPost) => {
            setLimitPost(newPost.slice(startPage - 1, currentPage));
            setPost(newPost);
        };

        const handlePostAdded = (newPost) => {
            setLimitPost(newPost.slice(startPage - 1, currentPage));
            setPost(newPost);
        };

        const handleCommentAdded = (newPost) => {
            setLimitPost(newPost.slice(startPage - 1, currentPage));
            setPost(newPost);
        }

        socket.once("getpost", handleGetPost);
        socket.on("postAdded", handlePostAdded);
        socket.on("commentAdded", handleCommentAdded);

        return () => {
            socket.off("getpost", handleGetPost);
            socket.off("postAdded", handlePostAdded);
            socket.off("commentAdded", handleCommentAdded);
        };
    }, []);

    function fetchMoreData() {
        setTimeout(function () {
            // Tăng thêm 2 post
            let currentPage = limitPost.length + 2; // Thay đổi từ 3 thành 2
            const posts = post.slice(currentPage - 3, currentPage);

            setCurrentPage(currentPage);

            // Cập nhật limitPost bằng việc nối posts vào nó
            setLimitPost([...limitPost, ...posts]);

            console.log(limitPost.length)
        }, 1000);
    }

    return (
        <>
            <InfiniteScroll
                dataLength={post.length}
                next={fetchMoreData}
                hasMore={true}
                loader={<h4>Loading...</h4>}
            >
                {
                    limitPost.map((item, index) => (
                        <Post
                            key={index}
                            _id={item._id}
                            sender={item.sender}
                            content={{
                                text: item.content.text,
                                image: item.content.image !== "" ? `http://127.0.0.1:3001/uploads/${item.content.image}` : "",
                            }}
                            creationTime={item.creationTime}
                            comments={item.comments}
                        />
                    ))
                }
            </InfiniteScroll>
            {/* Thêm giao diện để tạo bài viết mới và gọi handleNewPost khi bài viết được tạo */}
        </>
    );
}
