import React, { useEffect, useState } from 'react';

function Register() {
    const [form, setForm] = useState(
        {
            firstName: "",
            lastName: "",
            username: "",
            password: "",
            confirmPassword: ""
        }
    )
    const [isValid, setIsValid] = useState(false);

    useEffect(() => {
        import('../styles/login.css')
    }, [])

    function handleInputChange(e) {
        const { id, value } = e.target;

        if (id === "firstName") {
            setForm.firstName(value);
        }
        if (id === "lastName") {
           setForm.lastName(value)
        }
        if (id === "username") {
           setForm.username(value)
        }
        if (id === "password") {
            setForm.password(value)
        }

        for (const key in form) 
        {
            if (form[key] === "") {
              setIsValid(false);
              return;
            }
        }
    }

    function handleSubmit(e) {
        e.preventDefault();
        
        console.log('Form submitted', form);    
    }

    return (
        <div className="wrapper">
            <form onSubmit = {handleSubmit}>
                <h1>Register</h1>
                <div className="input-box row">
                    <input id='firstName'
                        className='mr-15'
                        type="text"
                        placeholder="First Name"
                        value={form.firstName}
                        onChange={(e) => handleInputChange(e)}
                    />
                    <input id='lastName'
                        type="text"
                        placeholder="Last Name"
                        value={form.lastName}
                        onChange={(e) => handleInputChange(e)}
                    />
                </div>
                <div className="input-box">
                    <input id='username'
                        type="text"
                        placeholder="Username"
                        value={form.username}
                        onChange={(e) => handleInputChange(e)}
                    />
                    <span id="img-user" className="text-white material-symbols-outlined">
                        person
                    </span>
                </div>
                <div className="input-box">
                    <input id="password"
                        type="password"
                        placeholder="Password"
                        value={form.password}
                        onChange={(e) => handleInputChange(e)}
                    />
                    <span id="img-lock" className="text-white material-symbols-outlined">
                        key
                    </span>
                </div>
                <div className="input-box">
                    <input
                        id="confirmPassword"
                        type="password"
                        placeholder="Confirm Password"
                        onChange={(e) => handleInputChange(e)}
                    />
                    <span id="img-lock" className="text-white material-symbols-outlined">
                        key
                    </span>
                </div>
                <button type="submit" className={
                    (isValid) ? 'btn' : 'btn btn-disable'
                }>
                    Register
                </button>
            </form>
        </div>
    )
}

export default Register;
