import { useState, useEffect } from 'react';
import { getPost } from '../adapters/post'
import Post from './Post'
import io from 'socket.io-client';

const socket = io('http://127.0.0.1:4000', { transports : ['websocket'] });

export default function PostList() {
    const [post, setPost] = useState([]);
    
    useEffect(() => {
        socket.on('newPost', () => 
        {
            update()
        })

        update()
    }, []);

    async function update () {
        const post = await getPost();
        setPost(post);
    }

    return (
        <>
            {
                post.map((item, index) => (
                    <Post
                        key={index}
                        user={{
                            name: item.sender,
                            image: "https://antimatter.vn/wp-content/uploads/2022/10/hinh-anh-gai-xinh-de-thuong-457x600.jpg"
                        }}
                        content={{
                            time: "12 hours ago",
                            image: (item.content.image !== "") ? `http://127.0.0.1:3001/uploads/${item.content.image}` : ""
                        }}
                        creationTime={item.creationTime}
                    />
                ))
            }
        </>
    )
}
