import React, { useEffect, useRef, useState } from "react";
import Headers from '../components/Header';
import ListChat from "../components/ListChat";
import TextareaAutosize from 'react-textarea-autosize';
import socket from "../socket/socket-client";
import Message from "../components/Message";

// Import CSS styles
import chatStyle from '../styles/chat.css';
import homepageStyle from '../styles/homepage.css';
import generalStyle from '../styles/general.css';

export default function Chat() {
    // State variables
    const [server, setServer] = useState("");
    const [message, setMessage] = useState("");
    const [selectedFriend, setSelectedFriend] = useState(null);

    const userRef = useRef();

    useEffect(() => {
        const session = JSON.parse(sessionStorage.getItem("userAuth"));
        userRef.current = session.user;
        setServer(`${process.env.REACT_APP_SEVER_IP}:${process.env.REACT_APP_SERVER_PORT}`);

        

    }, []);

    useEffect(() => {
        if (selectedFriend)
        {
           // Mặc định selectedFriend = 0
           console.log(selectedFriend)
        }

    }, [selectedFriend])

    function sendMessage() {
        const data = {
            'sender': userRef.current,
            'content': message,
            'creationTime': new Date()
        }

      

        socket.emit("sendMessage", data);
    }

    return (
        <div id="app">
            <Headers customStyle='header-chat' title="Message" />

            <div id="wrap-message" className="row">
                <ListChat setSelectedFriend={setSelectedFriend} />

                {/* Chat Box */}
                <div className="parent flex-1 mt-15">
                    <div id="boxChat" className="row justify-space-between mlr-32">

                        {/* Avatar and Name */}
                        <div className="row align-items-center">
                            <img
                                className="avatar"
                                src={`${server}/uploads/${selectedFriend.recipients.avatar}`}
                                alt="avatar"
                            />

                            <div className="ml-15">
                                <h3>{selectedFriend.recipients.firstName + " " + selectedFriend.recipients.lastName}</h3>
                                <p>Online</p>
                            </div>
                        </div>

                        {/* List Icons */}
                        <div className="row">
                            <a className="icon-effect" href="#">
                                <i className="fa-regular fa-envelope" />
                            </a>
                            <a className="icon-effect" href="#">
                                <i className="fa-solid fa-gears" />
                            </a>
                            <a className="icon-effect" href="#">
                                <i className="fa-solid fa-gears" />
                            </a>
                        </div>
                    </div>

                    {/* Chat Messages View */}
                    <div id="viewchat" className="mlr-32">
                        {/* Messages will be displayed here */}
                       
                    </div>

                    {/* Message Input Box */}
                    <div id="boxMessage" className="row">
                        <TextareaAutosize
                            className="post-comment ml-13"
                            placeholder="Bạn đang nghĩ gì ?"
                            onChange={e => setMessage(e.target.value)}
                        />
                        <img onClick={sendMessage} id="btn" width="36" height="36" src="https://img.icons8.com/sf-regular/48/paper-plane.png" alt="paper-plane" />
                    </div>
                </div>
            </div>
        </div>
    )
}
